function FeatureFlagAdmin() {
    const [flags, setFlags] = useState([]);

    async function toggleFlag(flagKey: string, newValue: boolean) {
        await fetch(`/api/feature-flags/${flagKey}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ value: newValue })
        });
        loadFlags();
    }

    return (
        &lt;div&gt;
            &lt;h2&gt;Feature Flags&lt;/h2&gt;
            {flags.map(flag =&gt; (
                &lt;div key={flag.flag_key}&gt;
                    &lt;Toggle
                        id={flag.flag_key}
                        labelText={flag.description}
                        toggled={flag.flag_value}
                        onToggle={(checked) =&gt; toggleFlag(flag.flag_key, checked)}
                    /&gt;
                &lt;/div&gt;
            ))}
        &lt;/div&gt;
    );
}





